mapper.select:
SELECT c.coach_id, c.club_id, club.club_name, c.user_id, u.actual_name as user_name, c.coach_no, c.avatar_url, c.birth_date, c.id_card, c.entry_date, c.specialties, c.rider_cert_no, c.rider_show_jumping_level, c.rider_dressage_level, c.rider_eventing_level, c.coach_cert_no, c.coach_star_level, c.coach_fee, c.sort_order, c.create_by, c.create_time, c.is_valid FROM m_coach c LEFT JOIN m_club club ON c.club_id = club.club_id AND club.is_delete = 0 LEFT JOIN t_employee u ON c.user_id = u.employee_id AND u.deleted_flag = 0
c.is_delete = #{queryForm.isDelete}
AND ( INSTR(c.coach_no, #{queryForm.keywords}) )
AND c.club_id = #{queryForm.clubId}
AND c.user_id = #{queryForm.userId}
AND c.coach_star_level = #{queryForm.coachLevel}
AND DATE_FORMAT(c.create_time, '%Y-%m-%d') >= #{queryForm.startDate}
AND DATE_FORMAT(c.create_time, '%Y-%m-%d') <= #{queryForm.endDate}
AND c.is_valid = #{queryForm.isValid}
ORDER BY c.sort_order ASC, c.create_time DESC

SELECT c.coach_id, c.club_id, club.club_name, c.user_id, u.actual_name as user_name, c.coach_no, c.avatar_url, c.birth_date, c.id_card, c.entry_date, c.specialties, c.rider_cert_no, c.rider_show_jumping_level, c.rider_dressage_level, c.rider_eventing_level, c.coach_cert_no, c.coach_star_level, c.coach_fee, c.sort_order, c.create_by, c.create_time, c.is_valid FROM m_coach c LEFT JOIN m_club club ON c.club_id = club.club_id AND club.is_delete = 0 LEFT JOIN t_employee u ON c.user_id = u.employee_id AND u.deleted_flag = 0 WHERE c.is_delete = 0
AND c.is_valid = #{isValid}
AND c.club_id = #{clubId}
ORDER BY c.sort_order ASC, c.create_time DESC
