SELECT h.horse_id, h.club_id, c.club_name, h.horse_name, h.horse_code, h.breed, h.gender, h.color, h.height, h.weight, h.birth_date, h.chip_no, h.passport_no, h.horse_type, h.owner_id, member.actual_name as owner_name, h.responsible_coach_id, coach.actual_name as responsible_coach_name, h.responsible_groom_id, u_groom.actual_name as responsible_groom_name, h.boarding_start_date, h.boarding_end_date, h.boarding_fee, h.health_status, h.work_status, h.create_time, h.create_by FROM m_horse h LEFT JOIN m_club c ON h.club_id = c.club_id AND c.is_delete = 0 AND c.is_valid = 1 LEFT JOIN m_member member ON h.owner_id = member.member_id AND member.is_delete = 0 AND member.is_valid = 1 LEFT JOIN m_coach coach ON h.responsible_coach_id = coach.coach_id AND coach.is_delete = 0 AND coach.is_valid = 1 LEFT JOIN t_employee u_groom ON h.responsible_groom_id = u_groom.employee_id AND u_groom.deleted_flag = 0
h.is_delete = #{query.isDelete} AND h.is_valid = #{query.isValid}
AND (h.horse_name LIKE CONCAT('%', #{query.keywords}, '%') OR h.horse_code LIKE CONCAT('%', #{query.keywords}, '%') OR h.chip_no LIKE CONCAT('%', #{query.keywords}, '%'))
AND h.club_id = #{query.clubId}
AND h.breed LIKE CONCAT('%', #{query.breed}, '%')
AND h.gender = #{query.gender}
AND h.horse_type = #{query.horseType}
AND h.owner_id = #{query.ownerId}
AND h.responsible_coach_id = #{query.responsibleCoachId}
AND h.health_status = #{query.healthStatus}
AND h.work_status = #{query.workStatus}
AND h.create_time >= #{query.startDate}
AND h.create_time <= #{query.endDate}
ORDER BY h.create_time DESC

SELECT h.horse_id, h.club_id, c.club_name, h.horse_name, h.horse_code, h.breed, h.gender, h.color, h.height, h.weight, h.birth_date, h.chip_no, h.passport_no, h.horse_type, h.owner_id, member.actual_name as owner_name, h.responsible_coach_id, coach.actual_name as responsible_coach_name, h.responsible_groom_id, u_groom.actual_name as responsible_groom_name, h.boarding_start_date, h.boarding_end_date, h.boarding_fee, h.health_status, h.work_status, h.create_time, h.create_by FROM m_horse h LEFT JOIN m_club c ON h.club_id = c.club_id AND c.is_delete = 0 AND c.is_valid = 1 LEFT JOIN m_member member ON h.owner_id = member.member_id AND member.is_delete = 0 AND member.is_valid = 1 LEFT JOIN m_coach coach ON h.responsible_coach_id = coach.coach_id AND coach.is_delete = 0 AND coach.is_valid = 1 LEFT JOIN t_employee u_groom ON h.responsible_groom_id = u_groom.employee_id AND u_groom.deleted_flag = 0 WHERE h.is_delete = 0 AND h.is_valid = 1
AND h.club_id = #{clubId}
AND h.horse_type = #{horseType}
ORDER BY h.horse_name ASC

SELECT h.horse_id, h.club_id, c.club_name, h.horse_name, h.horse_code, h.breed, h.gender, h.color, h.height, h.weight, h.birth_date, h.chip_no, h.passport_no, h.horse_type, h.owner_id, CASE WHEN h.horse_type = 1 THEN c.club_name WHEN h.horse_type = 2 THEN member.actual_name WHEN h.horse_type = 3 THEN coach.actual_name ELSE '-' END as owner_name, h.responsible_coach_id, coach.actual_name as responsible_coach_name, h.responsible_groom_id, u_groom.actual_name as responsible_groom_name, h.boarding_start_date, h.boarding_end_date, h.boarding_fee, h.health_status, h.work_status, h.create_time, h.create_by FROM m_horse h LEFT JOIN m_club c ON h.club_id = c.club_id AND c.is_delete = 0 AND c.is_valid = 1 LEFT JOIN m_member member ON h.owner_id = member.member_id AND member.is_delete = 0 AND member.is_valid = 1 LEFT JOIN m_coach coach ON h.responsible_coach_id = coach.coach_id AND coach.is_delete = 0 AND coach.is_valid = 1 LEFT JOIN t_employee u_groom ON h.responsible_groom_id = u_groom.employee_id AND u_groom.deleted_flag = 0 WHERE h.is_delete = 0 AND h.is_valid = 1 AND h.club_id = #{clubId}
AND h.update_time BETWEEN #{startDate} AND #{endDate}
ORDER BY h.horse_name ASC
